public class WiFiTest {

    private static final String URL = "http://cds.51y5.net/feeds.sec";
    // http://cds.51y5.net/feeds.sec?st=m&appId=A0008&sign=A51EBF1EE22D8002B9C18E6543C07BE4&et=a&pid=cds001001%2Ccds001002%2Ccds003001%2Ccds004001%2Ccds004002&

    static Map<String, String> params = new HashMap<String, String>();

    public static void saveToFile(String text, String path, boolean isClose) {
        File file = new File("runlog.txt");
        BufferedWriter bf = null;
        try {
            FileOutputStream outputStream = new FileOutputStream(file, true);
            OutputStreamWriter outWriter = new OutputStreamWriter(outputStream);
            bf = new BufferedWriter(outWriter);
            bf.append(text);
            bf.newLine();
            bf.flush();

            if (isClose)
                bf.close();
        } catch (FileNotFoundException e1) {
            e1.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static void getWiFiData() throws Exception {
        Date d = new Date();
        params.put("st", "m");
        params.put("appId", "A0008");
        params.put("sign", "A51EBF1EE22D8002B9C18E6543C07BE4");
        params.put("et", "a");
        params.put("pid", "cds001001%2Ccds001002%2Ccds003001%2Ccds004001%2Ccds004002");
        params.put("ed",
                "");

        HttpRequester request = new HttpRequester();
        request.setDefaultContentEncoding("UTF-8");
        HttpRespons hr = request.sendGet(URL, params);

        String json = hr.getContent();
        
        System.out.println(json);

        if (json == null || json.equals("")) {
            return;
        } else {
            
        }

        JSONObject obj = JSONObject.fromObject(json);

        JSONObject cds001001 = obj.getJSONObject("cds001001");
        JSONArray results = cds001001.getJSONArray("result");

        for (int i = 0; i < results.size(); i++) {
            String feedback = "" + d + ",";
            JSONObject result = results.getJSONObject(i);
            JSONArray items = result.getJSONArray("item");
            JSONObject item = items.getJSONObject(0);
            String title = item.getString("title");
            if (title.contains("视频")) {
                continue;
            }
            if (title.contains(",")) {
//                System.out.println(title);
                title =  title.replaceAll(",", "，");
//                System.out.println(title);
            }
            // System.out.print(title);
            // System.out.print(",");
            feedback = feedback + title + ",";
            JSONArray imgs = null;
            try {
                imgs = item.getJSONArray("imgs");
            } catch (Exception e) {
               continue;
            }
            
            for (int j = 0; j < 3; j++) {
                if (j < imgs.size()) {
                    JSONObject img = imgs.getJSONObject(j);
                    String url = img.getString("url");
                    if (url.contains(",")) {
                        url = url.replaceAll(",", "，");
                    }
                    // System.out.print(url);
                    // System.out.print(",");
                    feedback = feedback + url + ",";
                } else {
                    // System.out.print(",");
                    feedback = feedback + ",";
                }
            }
            JSONArray tags = null;
            try {
                tags = item.getJSONArray("tags");
            } catch (Exception e) {
                continue;
            }
            for (int j = 0; j < 3; j++) {
                if (j < tags.size()) {
                    JSONObject tag = tags.getJSONObject(j);
                    String text = tag.getString("text");
//                    if (text.contains("广告")) {
//                        continue;
//                    }
                    // System.out.print(text);
                    // System.out.print(",");
                    feedback = feedback + text + ",";
                } else {
                    // System.out.print(",");
                    feedback = feedback + ",";
                }
            }
//             System.out.println(feedback);
            saveToFile(feedback, "runlog.txt", false);
        }
    }

    public static String getConfigProperty(String key) {
        ConfigProperty cp = new ConfigProperty("config.properties");
        String value = cp.getProperty(key);

        return value;
    }

    public static void main(String[] args) throws Exception {
            getWiFiData();
    }
}
